import axios from 'axios';

const api = axios.create({
    baseURL: process.env.NODE_ENV === 'development' ? '/api' : process.env.REACT_APP_API_URL,
    headers: {
        'Content-Type': 'application/json',
        'ngrok-skip-browser-warning': 'true'
    },
    withCredentials: false
});

// 요청 인터셉터
api.interceptors.request.use(
    (config) => {
        const uid = localStorage.getItem('uid');
        if (uid && !config.url.includes('uid=')) {
            const separator = config.url.includes('?') ? '&' : '?';
            config.url = `${config.url}${separator}uid=${uid}`;
        }
        return config;
    },
    (error) => Promise.reject(error)
);

// 응답 인터셉터
api.interceptors.response.use(
    (response) => response,
    async (error) => {
        if (error.response?.status === 401) {
            // 토큰 갱신 로직
            try {
                const refreshToken = localStorage.getItem('refreshToken');
                if (refreshToken) {
                    const response = await api.post('/refresh_token', { refresh_token: refreshToken });
                    localStorage.setItem('token', response.data.token);
                    error.config.headers['Authorization'] = `Bearer ${response.data.token}`;
                    return api(error.config);
                }
            } catch (refreshError) {
                localStorage.clear();
                window.location.href = '/login';
            }
        }
        return Promise.reject(error);
    }
);

export default api;
