import React, { useEffect, useState } from 'react';
import { getCollectedProducts, batchTaobaoMatch, downloadHeySeller, generateSEO } from '../utils/api';
import LoadingSpinner from './LoadingSpinner';
import '../styles/main.css';

function CollectedProducts() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCategories, setShowCategories] = useState(false);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [retryCount, setRetryCount] = useState(0);

  useEffect(() => {
    fetchCollectedProducts();
  }, []);

  const fetchCollectedProducts = async () => {
    try {
      setLoading(true);
      setError(null);

      const result = await getCollectedProducts();

      if (!result || !result.products) {
        throw new Error('상품 데이터를 불러올 수 없습니다.');
      }

      setProducts(result.products);

      if (result.products.length === 0 && retryCount < 3) {
        setTimeout(() => {
          setRetryCount(prev => prev + 1);
          fetchCollectedProducts();
        }, 3000);
      }

    } catch (error) {
      console.error('수집된 상품 조회 실패:', error);
      setError(error.response?.data?.error || '수집된 상품을 불러오는 데 실패했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleProductSelect = (productId) => {
    setSelectedProducts(prev => {
      if (prev.includes(productId)) {
        return prev.filter(id => id !== productId);
      }
      return [...prev, productId];
    });
  };

  const handleTaobaoMatch = async () => {
    if (selectedProducts.length === 0) {
      alert('매칭할 상품을 선택해주세요.');
      return;
    }

    try {
      setLoading(true);
      const result = await batchTaobaoMatch(selectedProducts);
      setProducts(result.matched_products);
      alert('타오바오 매칭이 완료되었습니다.');
    } catch (error) {
      console.error('타오바오 매칭 실패:', error);
      alert(error.response?.data?.error || '타오바오 매칭 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (url) => {
    if (!url) return '';
    return url.startsWith('/') ? `https://shopping.naver.com${url}` : url;
  };

  if (loading) return <LoadingSpinner message="수집된 상품을 불러오는 중..." />;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="collected-products">
      <h2>수집된 상품 ({products.length})</h2>

      <div className="action-buttons">
        <button 
          onClick={handleTaobaoMatch} 
          className="collect-button" 
          disabled={selectedProducts.length === 0}
        >
          타오바오 매칭
        </button>
        <button 
          onClick={() => setShowCategories(!showCategories)} 
          className="toggle-categories-button"
        >
          {showCategories ? '카테고리 숨기기' : '카테고리 보기'}
        </button>
      </div>

      {products.length > 0 ? (
        <div className="products-table-container">
          <table className="products-table">
            <thead>
              <tr>
                <th>선택</th>
                <th>이미지</th>
                <th>상품명</th>
                <th>가격</th>
                <th>마켓명</th>
                {showCategories && <th>카테고리</th>}
                <th>수집일</th>
                <th>액션</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id} className={selectedProducts.includes(product.id) ? 'selected' : ''}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedProducts.includes(product.id)}
                      onChange={() => handleProductSelect(product.id)}
                    />
                  </td>
                  <td>
                    <img 
                      src={getImageUrl(product.image_url)} 
                      alt={product.title} 
                      className="product-image"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.png';
                      }}
                    />
                  </td>
                  <td>{product.title || product.product_title}</td>
                  <td>{product.price?.toLocaleString()}원</td>
                  <td>{product.mall_name}</td>
                  {showCategories && <td>{product.category || '-'}</td>}
                  <td>{new Date(product.collection_date).toLocaleDateString()}</td>
                  <td>
                    <a 
                      href={product.product_url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="view-button"
                    >
                      보기
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="no-products">
          <p>수집된 상품이 없습니다.</p>
          {retryCount > 0 && <p>데이터를 불러오는 중입니다. 잠시만 기다려주세요...</p>}
        </div>
      )}
    </div>
  );
}

export default CollectedProducts;
